import { createStore } from 'vuex'
import axios from 'axios';

export default createStore({
  state() {
    return {
      questionList: [],
      itemNum: 1,
    }
  },
  getters: {},
  mutations: {
    getQuestionList(state, list) {
      state.questionList = list;
    }
  },
  actions: {
    getQuestionListAction(context) {
      axios.get('https://www.fastmock.site/mock/3a33c631bcba043a378b4ceafbf994b8/question/fri')
        .then(res => {
          // 检查 API 返回的数据结构，确保包含你期望的 list 数据
          const list = res.data; // 假设返回的数据包含在 res.data 中

          // 触发 getQuestionList mutation，并传递数据
          context.commit('getQuestionList', list);
        })
        .catch(error => {
          console.error('Error fetching question list:', error);
        });
    }
  },
  modules: {}
})
