import { createStore } from 'vuex'
import axios from 'axios';
export default createStore({
  state() { //全局的data
    return{
      questionList: [],

    }
  },
  getters: {  //全局的computed
  },
  mutations: {  //methods 同步的方式去修改数据源
    getQuestionList(state, list){
      state.questionList = list
    }
  },
  actions: {  //async methods
     getQuestionListAction(context){//context = store
      axios.get('https://www.fastmock.site/mock/3a33c631bcba043a378b4ceafbf994b8/question/fri')
      .then(res => {
        console.log(res);
        //触发getQuestionList
        context.commit('getQuestionList')
      })

    }
  },
  modules: {
  }
})
